import { useState } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState(0);

  const handleDecrease = () => {
    setCount(count - 1);
  };

  const handleIncrease = () => {
    setCount(count + 1);
  };

  const handleReset = () => {
    setCount(0);
  };

  return (
    <div className="w-screen h-screen flex justify-center items-center bg-claret flex-col gap-10">
      <div className="text-charcoal font-medium text-2xl">Increment & Decrement</div>
      <div className="bg-white flex justify-center gap-12 py-2 rounded-sm text-5xl text-slate">
        <button onClick={handleDecrease} className="border-r-2 text-center w-20 py-5 border-gray-300">
          -
        </button>
        <div className="font-bold text-center">{count}</div>
        <button onClick={handleIncrease} className="border-l-2 text-center w-20 py-5 border-gray-300">
          +
        </button>
      </div>
      <button onClick={handleReset} className="bg-blue-500 text-white px-5 py-2 rounded-sm text-lg">
        Reset
      </button>
    </div>
  );
}

export default App;
